import NextImage, { ImageProps } from 'next/image';
import { useState } from 'react';

interface OptimizedImageProps extends Omit<ImageProps, 'loader'> {
  quality?: number;
  blur?: boolean;
}

export default function Image({ 
  quality = 85, 
  blur = true,
  onLoad,
  className = '',
  ...props 
}: OptimizedImageProps) {
  const [isLoading, setIsLoading] = useState(true);

  // Custom loader function to handle image optimization
  const customLoader = ({ src, width, quality: q }: { src: string; width: number; quality?: number }) => {
    // Handle both local and external images
    if (src.startsWith('http')) {
      return src;
    }
    // For local images, add width and quality parameters
    return `${src}?w=${width}&q=${q || quality}`;
  };

  return (
    <div className={`relative overflow-hidden ${className}`}>
      <NextImage
        {...props}
        className={`
          duration-700 ease-in-out
          ${isLoading ? 'scale-110 blur-lg' : 'scale-100 blur-0'}
          ${className}
        `}
        loader={customLoader}
        quality={quality}
        onLoadingComplete={() => setIsLoading(false)}
        placeholder={blur ? 'blur' : 'empty'}
        blurDataURL="data:image/jpeg;base64,"
      />
    </div>
  );
}